#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\Portfolio\node_modules\.pnpm\next-sitemap@4.2.3_next@15.1.4\node_modules\next-sitemap\bin\node_modules;E:\Portfolio\node_modules\.pnpm\next-sitemap@4.2.3_next@15.1.4\node_modules\next-sitemap\node_modules;E:\Portfolio\node_modules\.pnpm\next-sitemap@4.2.3_next@15.1.4\node_modules;E:\Portfolio\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/Portfolio/node_modules/.pnpm/next-sitemap@4.2.3_next@15.1.4/node_modules/next-sitemap/bin/node_modules:/mnt/e/Portfolio/node_modules/.pnpm/next-sitemap@4.2.3_next@15.1.4/node_modules/next-sitemap/node_modules:/mnt/e/Portfolio/node_modules/.pnpm/next-sitemap@4.2.3_next@15.1.4/node_modules:/mnt/e/Portfolio/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../next-sitemap/bin/next-sitemap.cjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../next-sitemap/bin/next-sitemap.cjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../next-sitemap/bin/next-sitemap.cjs" $args
  } else {
    & "node$exe"  "$basedir/../next-sitemap/bin/next-sitemap.cjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
